import React, { useEffect, useState } from 'react'
import './Category.css'
import { FiChevronDown } from 'react-icons/fi'
import Channel from './Channel'
import db from './firebase'
import { useDispatch, useSelector } from 'react-redux'
import { selectCategoryId, setCategoryInfo } from './features/counter/appSlice'

function Category({ id, title }) {
    const categoryId = useSelector(selectCategoryId)
    const [channels, setChannels] = useState([])
    const dispatch = useDispatch();

    const addChannel = () => {
        const channelName = prompt('Add channel:');
        if (channelName) {
            db.collection('categories').doc(categoryId).collection('channels').add({
                channelName: channelName,
            });
        }

    }

    useEffect(() => {
        if (categoryId) {
            db.collection('categories').doc(categoryId).collection('channels').orderBy("timestamp", "desc").onSnapshot((snapshot) =>
                setChannels(snapshot.docs.map((doc) => ({
                    id: doc.id,
                    channel: doc.data()
                }))));
        }

    }, [])
    return (
        <div className='category' onClick={() => {
            dispatch(
                setCategoryInfo({
                    categoryId: id,
                    categoryName: title,
                })
            )
        }}>
            <div className='category_header'>
                <div className='category_header_title'>
                    <FiChevronDown />
                    <p>{title}</p>
                </div>
                <div className='category_header_addChannel'>
                    <p>+</p>
                </div>
            </div>
            <div className='category_channels'>
                <button onClick={addChannel}>+_+</button>
                {channels.map(({ id, title }) => (
                    <Channel key={id} title={title.channelName} />
                ))}
                {/* <Channel />
                <Channel />
                <Channel /> */}
            </div>
        </div>
    )
}

export default Category
