import React from 'react'
import './Message.css';
import Avatar from './Avatar'
import { useSelector } from 'react-redux';
import { selectUser } from './features/counter/userSlice';

function Message({ id, message, nick, date, avatarColor }) {
    // const user = useSelector(selectUser);

    // let avatarColor = 'rgba(0, 0, 0, 0)';
    // if (user) avatarColor = user.avatarColor;
    let rand = Math.random();
    if (rand > 0.5) {
        return `rgb(255, ${Math.ceil(Math.random() * 255)}, 0)`
    }
    else if (rand < 0.5) {
        return `rgb(255, 0, ${Math.ceil(Math.random() * 255)})`
    }
    else if (rand < 0.5) {
        return `rgb(${Math.ceil(Math.random() * 255)}, 255, 0)`
    }
    else if (rand < 0.5) {
        return `rgb(0, 255, ${Math.ceil(Math.random() * 255)})`
    }
    else if (rand < 0.5) {
        return `rgb(${Math.ceil(Math.random() * 255)}, 0, 255)`
    }
    else if (rand < 0.5) {
        return `rgb(0, ${Math.ceil(Math.random() * 255)}, 255)`
    }
    return (
        <div className='message'>
            <Avatar avatarColor={avatarColor} />
            <div className='message_info'>
                <div className='message_info_title'>
                    <p className='message_info_title_nick' style={{ color: 'rgb(255, 0, 0)' }}>{nick}</p>
                    <p className='message_info_title_date'>{date}</p>
                </div>
                <div className='message_info_text'>
                    <p>{message}</p>
                </div>
            </div>

        </div>
    )
}

export default Message
