import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { selectServerId, setServerInfo } from './features/counter/appSlice';
import './ServerIcon.css'
import { BsX } from "react-icons/bs";
import db from './firebase';


function ServerIcon({ id, title }) {
    const dispatch = useDispatch();
    const serverId = useSelector(selectServerId);

    const removeServer = () => {
        const server = db.collection('servers').doc(selectServerId)
    }
    return (
        <div className='serverIcon' onClick={() => {
            dispatch(
                setServerInfo({
                    serverId: id,
                    serverName: title,
                })
            )
        }}>
            <p className='serverIcon_title'>{title ? title[0] : null}</p>
            <div className='serverIcon_remove' onClick={() => alert('remove server')}><BsX /></div>
        </div>
    )
}

export default ServerIcon
