import React from 'react'
import './AddServerIcon.css'
import { HiOutlinePlus } from 'react-icons/hi';
import db from './firebase';
import firebase from 'firebase';

function AddServerIcon() {

    // add server to database
    const addServer = () => {
        const serverName = prompt('Enter server name: ');
        if (serverName) {
            db.collection('servers').add({
                timestamp: firebase.firestore.FieldValue.serverTimestamp(),
                serverName: serverName,
            });
        }
    }
    return (
        <div className='addServerIcon' onClick={addServer}>
            <HiOutlinePlus className='addServerIcon_icon' />
            <div className='addServerIcon_alert'>
                <p>Add server</p>
            </div>
            <div className='addServerIcon_prompt' onClick={(e) => e.stopPropagation()}>
                <div className='addServerIcon_prompt_field'>
                    <p className='addServerIcon_prompt_field_title'>Enter server name:</p>
                    <input className='addServerIcon_prompt_field_input' />
                    <button className='addServerIcon_prompt_field_cancel'>Cancel</button>
                    <button className='addServerIcon_prompt_field_submit'>Create</button>
                </div>
            </div>
        </div>
    )
}

export default AddServerIcon
