import React, { useState, useEffect } from 'react'
import './ChannelsList.css'
import UserContainer from './UserContainer'
import Category from './Category'
import ChannelsListHeader from './ChannelsListHeader'
import db from './firebase'

function ChannelsList() {
    const [channels, setChannels] = useState([])
    const [channelName, setChannelName] = useState()

    const addChannel = () => {
        let x = prompt('New channel name: ');
        setChannelName(x);
        console.log(x)
        db.collection('channels').add({
            channelName,
        })
    }

    useEffect(() => {
        db.collection('channels').onSnapshot((snapshot) => {
            setChannels(
                snapshot.docs.map((doc) => ({
                    id: doc.id,
                    channel: doc.data(),
                }))
            )
        })
    }, [])

    return (
        <div className='channelsList'>
            <div className='channelsList_top'>
                <ChannelsListHeader title='Channel' />
                <div className='channelsList_body'>
                    <button onClick={addChannel}>+</button>
                    {channels.map(({ id, channel }) => (
                        <Category key={id} title={channel.channelName} />
                    ))}
                </div>
            </div>
            <UserContainer />
        </div>
    )
}

export default ChannelsList
