import React, { useState, useEffect } from 'react'
import './ChannelsList.css'
import UserContainer from './UserContainer'
import Category from './Category'
import ChannelsListHeader from './ChannelsListHeader'
import db from './firebase'

function ChannelsList() {
    const [categories, setCategories] = useState([])
    const [categoryName, setCategoryName] = useState()

    const addCategory = () => {
        setCategoryName(prompt('New category name: '));
        // console.log(x)
        db.collection('categories').add({
            categoryName,
        })
    }

    useEffect(() => {
        db.collection('categories').onSnapshot((snapshot) => {
            setCategories(
                snapshot.docs.map((doc) => ({
                    id: doc.id,
                    category: doc.data(),
                }))
            )
        })
    }, [])

    return (
        <div className='channelsList'>
            <div className='channelsList_top'>
                <ChannelsListHeader title='Channel' />
                <div className='channelsList_body'>
                    <button onClick={addCategory}>+</button>
                    {categories.map(({ id, category }) => (
                        <Category key={id} title={category.categoryName} />
                    ))}
                </div>
            </div>
            <UserContainer />
        </div>
    )
}

export default ChannelsList
