import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { selectServerId, setServerInfo, setChannelInfo, setCategoryInfo } from './features/counter/appSlice';
import './ServerIcon.css'
import { BsX } from "react-icons/bs";
import db from './firebase';
import { useHistory } from 'react-router-dom';


function ServerIcon({ id, title }) {
    const dispatch = useDispatch();
    const serverId = useSelector(selectServerId);
    const history = useHistory();

    const removeServer = () => {
        const server = db.collection('servers').doc(serverId);
        const category = server.collection('categories');

        category.get().then(res => {
            res.forEach(element => {
                element.ref.delete();
            });
        });
        server.delete();
        dispatch(
            setChannelInfo({
                channelId: null,
            }),
            setCategoryInfo({
                categoryId: null,
            }),
            setServerInfo({
                serverId: null,
            })
        );
        history.push('/');
    }
    return (
        <div className='serverIcon' onClick={() => {
            dispatch(
                setServerInfo({
                    serverId: id,
                    serverName: title,
                })
            )
        }}>
            <p className='serverIcon_title'>{title ? title[0] : null}</p>
            {
                id === serverId ?
                    <div className='serverIcon_remove' onClick={(e) => { e.stopPropagation(); removeServer() }}><BsX /></div>
                    :
                    null
            }

        </div >
    )
}

export default ServerIcon
