import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { selectServerId, setServerInfo, selectCategoryId } from './features/counter/appSlice';
import './ServerIcon.css'
import { BsX } from "react-icons/bs";
import db from './firebase';


function ServerIcon({ id, title }) {
    const dispatch = useDispatch();
    const serverId = useSelector(selectServerId);
    const categoryId = useSelector(selectCategoryId);

    const removeServer = () => {
        const server = db.collection('servers').doc(serverId);
        const category = server.collection('categories').doc(categoryId);
        const channel = category.collection('channels');

        channel.get().then(res => {
            res.forEach(element => {
                element.ref.delete();
            });
        });

        category.get().then(res => {
            res.forEach(element => {
                element.ref.delete();
            });
        });

        server.delete();
    }
    return (
        <div className='serverIcon' onClick={() => {
            dispatch(
                setServerInfo({
                    serverId: id,
                    serverName: title,
                })
            )
        }}>
            <p className='serverIcon_title'>{title ? title[0] : null}</p>
            {
                id === serverId ?
                    <div className='serverIcon_remove' onClick={removeServer}><BsX /></div>
                    :
                    null
            }

        </div>
    )
}

export default ServerIcon
